import React, { useState } from 'react';
import Statistics from './Statistics';

const App = () => {
  const [state, setState] = useState({
    good: 0,
    neutral: 0,
    bad: 0
  });

  const handleFeedback = (type) => {
    setState((prev) => ({ ...prev, [type]: prev[type] + 1 }));
  };

  return (
    <div>
      <h1>Кафе Expresso - Збір відгуків</h1>
      <button onClick={() => handleFeedback('good')}>Добре</button>
      <button onClick={() => handleFeedback('neutral')}>Нейтрально</button>
      <button onClick={() => handleFeedback('bad')}>Погано</button>
      <Statistics good={state.good} neutral={state.neutral} bad={state.bad} />
    </div>
  );
};

export default App;
